INSERT INTO dictionary (dictionaryid, pt, en, zh, website) VALUES ('vixiCsKycChecTitle3','6.3. As checagens de pessoas físicas compreendem uma potencial participação societária destas pessoas em sociedades empresariais.', '6.3 Checks of individuals include potential corporate participation of these individuals in business companies.', '??', 'VIXI');
