INSERT INTO dictionary (dictionaryid, pt, en, zh, website) VALUES ('vixiCsKycChecTitle1','6.1. A área de Compliance da Vixi Exchange realiza checagens automatizadas no momento do cadastro do cliente, bem como realiza checagens periódicas após ingresso na plataforma (via APIs).', '6.1 The Compliance area of Vixi Exchange performs automated checks at the time of customer registration, as well as performing periodic checks after entering the platform (via APIs).', '??', 'VIXI');
INSERT INTO dictionary (dictionaryid, pt, en, zh, website) VALUES ('vixiCsKycChecTitle2','6.2. A área de Compliance, na análise de KYC, checa, especialmente:', '6.2 The Compliance area, in the KYC analysis, checks especially:', '??', 'VIXI');
