INSERT INTO dictionary (dictionaryid, pt, en, zh, website) VALUES('vixiCsRiskTipText3Rel','relacionados ao uso racional de recursos, funcionamento de controles internos, eficácia dos processos de negócios e exposições financeiras, em nível de processo, incluindo-se os demais controles antifraude.',' related to the rational use of resources, functioning of internal controls, effectiveness of business processes and financial exposures, at the process level, including other anti-fraud controls.','??', 'VIXI');
