INSERT INTO dictionary (dictionaryid, pt, en, zh, website) VALUES ('vixiCsSeg3Text3','a Lei Complementar nº 105, de 10 de janeiro de 2001;', 'the Complementary Law No. 105, of January 10, 2001;', '??', 'VIXI');
INSERT INTO dictionary (dictionaryid, pt, en, zh, website) VALUES ('vixiCsSeg3BText4','LGPD:', 'LGPD:', '??', 'VIXI');
