CREATE TABLE menu (
  menuid       INTEGER      NOT NULL PRIMARY KEY,
  sequence     INTEGER      NOT NULL,
  text         VARCHAR(150) NOT NULL,
  header       VARCHAR(150),
  link         VARCHAR(150) NOT NULL,
  fathermenuid INTEGER,
  FOREIGN KEY (fathermenuid)
    REFERENCES MENU(menuid)
    ON DELETE CASCADE
);


